# -*- coding: utf-8 -*-from flask.ext.script import Manager, Server, Shellfrom flask.ext.script import prompt_boolfrom app.__init__ import make_app, dbfrom gunicornserver import GunicornServerfrom config import Developmentapp = make_app(Development)manager = Manager(app)@manager.commanddef db_setup():    # create all database tables    db.create_all()@manager.commanddef db_dropall():    # drop all databse tables    if prompt_bool("Are you sure ? You will lose all your data !"):        db.drop_all()@manager.commanddef test():    import unittest    tests = unittest.TestLoader().discover('tests')    unittest.TextTestRunner(verbosity=2).run(tests)manager.add_command("run", GunicornServer())manager.add_command("runserver", Server(host="0.0.0.0", port=80))if __name__ == '__main__':    manager.run(default_command="runserver")